import { useRoute, useRouteParameter, usePermission, useTranslation } from '@rocket.chat/ui-contexts';
import type { ReactElement } from 'react';
import React, { useCallback, useRef } from 'react';

import {
	Contextualbar,
	ContextualbarHeader,
	ContextualbarClose,
	ContextualbarDialog,
