						{isActionAttachment(attachment) && <ActionAttachment {...attachment} />}
					</>
				)}
			</AttachmentContent>
			{attachment.thumb_url && <AttachmentThumb url={attachment.thumb_url} />}
		</AttachmentBlock>
	);
};

export default DefaultAttachment;
