	const { t } = useTranslation();

	const retentionPolicy = useRetentionPolicy(room);

	const memoizedActions = useMemo(
		() => ({
			...(onClickEdit && {
				edit: {
					label: t('Edit'),
					action: onClickEdit,
					icon: 'edit' as const,
