				items: data.map((videoConf: any) => ({
					...videoConf,
					_updatedAt: new Date(videoConf._updatedAt),
					createdAt: new Date(videoConf.createdAt),
					endedAt: videoConf.endedAt ? new Date(videoConf.endedAt) : undefined,
				})),
				itemCount: total,
