		const wrapper = wrapperRef.current;

		if (!wrapper) {
			return false;
		}

		if (wrapper.scrollTop + scrollThreshold >= wrapper.scrollHeight - wrapper.clientHeight) {
			setNewLogsVisible(false);
			return true;
		}
		return false;
	}, []);

