							<FieldLabel htmlFor={scriptField}>{t('Script')}</FieldLabel>
							<FieldRow>
								<Controller
									name='script'
									control={control}
									render={({ field }) => (
										<TextAreaInput id={scriptField} {...field} rows={10} addon={<Icon name='code' size='x20' alignSelf='center' />} />
									)}
								/>
							</FieldRow>
						</Field>
					</FieldGroup>
				</AccordionItem>
			</Accordion>
		</Box>
	);
};

export default IncomingWebhookForm;

