									render={({ field }) => <Select {...field} id={formatField} placeholder={t('Format')} options={outputOptions} />}
								/>
							</FieldRow>
						</Field>
					</FieldGroup>
				</form>
			</ContextualbarScrollableContent>
			<ContextualbarFooter>
				<ButtonGroup stretch>
					<Button onClick={onCancel}>{t('Cancel')}</Button>
